const soundsArray = [
  {
    key: 'A',
    name: 'Clap',
    sound: 'sounds/clap.wav',
  },
  {
    key: 'S',
    name: 'Hihat',
    sound: 'sounds/hihat.wav',
  },
  {
    key: 'D',
